import React from 'react';
import {useState} from 'react';
import { Carousel } from 'react-bootstrap';
import './CarosuelWithPhotos.scss';
import HorseImgFirst from '../files/pictures/horse1.jpg';
import HorseImgSecond from '../files/pictures/horse2.jpg';
import HorseImgThird from '../files/pictures/horse3.jpg';

// example : https://react-bootstrap.github.io/components/carousel/#controlled
export const CarosuelWithPhotos = () => {
        const [index, setIndex] = useState(0);
        const [direction, setDirection] = useState(null);

        const handleSelect = (selectedIndex, e) => {
          setIndex(selectedIndex);
          setDirection(e.direction);
        };

        return (
          <Carousel activeIndex={index} direction={direction} onSelect={handleSelect}>
            <Carousel.Item>
              <img
                className="d-block w-100 change-color"
                src={HorseImgFirst}
                height={700}
                height={700}
                alt="First slide"
              />
              <Carousel.Caption>
                <h1 className="first-title">Stajnia dar</h1>
                <h1 className="second-title">Jazda konna</h1>
                {/* <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p> */}
              </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
              <img
                className="d-block w-100 change-color"
                src={HorseImgThird}
                height={700}
                height={700}
                alt="Second slide"
              />

              <Carousel.Caption>
              <h1 className="first-title">Stajnia dar</h1>
              <h1 className="first-title">Zawody</h1>
                {/* <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p> */}
              </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
              <img
                className="d-block w-100 change-color"
                src={HorseImgSecond}
                height={700}
                height={700}
                alt="Third slide"
              />

              <Carousel.Caption>
              <h1 className="first-title">Stajnia dar</h1>
              <h1 className="first-title">Trening koni</h1>
                {/* <p> Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p> */}
              </Carousel.Caption>
            </Carousel.Item>
          </Carousel>
        );
      }
